completedTasks.map:
function(doc) {
  if (doc.type === "task" && doc.app === "classify" && doc.completed === true) {
    emit ([doc.user, doc.list_name], doc);
  }
}

incompleteTasks.map:
function(doc) {
  if (doc.type === "task" && doc.app === "classify" && doc.completed === false) {
    emit (doc.user, doc);
  }
}

tasksByUserAndListName.map:
function(doc) {
  if (doc.type === "task" && doc.app === "classify") {
    emit ([doc.user, doc.list_name], doc);
  }
}
